sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
        var jsnParam = '{';
  //      gs.info('the sys_updated by is ' + current.sys_updated_by);
        // get active end integration end points 


        var groups = new GlideRecord('x_653938_interest_groups');
        groups.addQuery('group', current.assignment_group);
        groups.query();
	var firstJSON =0 
try{
        while (groups.next()) {
            var ep = new GlideRecord('x_653938_interest_target');

            ep.get(groups.remote_system);

            // get fields for remote system 
            var fields = new GlideRecord('x_653938_interest_fields');
            fields.addQuery('module', current.sys_class_name);
            fields.addQuery('target.name', ep.getValue('name'));
            fields.query();
            // 			var numRecs = fields.getRowCount();
            // 			var recNum = 0 ;

            while (fields.next()) {
                // 				recNum = recNum +1; 
           //     gs.info('looping in the fields for update' + fields.getValue('field_name'));

                if (current[fields.getValue('field_name')] != previous[fields.getValue('field_name')]) {
					if (firstJSON>0){
						jsnParam = jsnParam + ',';
					}

firstJSON = firstJSON + 1;
                    jsnParam = jsnParam + '"' + fields.getValue('field_name') + '":"' + current[fields.getValue('field_name')] + '"';

                }



                    if (!fields.hasNext()) {


                        jsnParam = jsnParam + '}';

                    }

   //            gs.info('BR update jsnParam is now ' + jsnParam);
            }
        }
 } catch (err) {
                    gs.info('BR update error is ' + err);
                }


        //create event for each remote system end point
 gs.info('BR update:event JSON string is =' + jsnParam);

      //  jsnParam = '[{"short_description":"' + current.short_description + '","description":"' + current.description + '","attempt":"0"}]';
       // jsnstr = JSON.stringify(jsnParam);

      //gs.info('BR update:event JSON string is =' + jsnstr);
        gs.eventQueue('x_653938_interest.incident_update', current, ep.getValue('sys_id'), jsnParam);
    }


    // 	if (!(current.short_description==previous.short_description)){
    // 	gs.info('short description changed');

    // 
)(current, previous);
