sysevent_script_action.script:
gs.info('PS: running script action');

try {

	// setup REST message to send over the newly created incident
    var r = new sn_ws.RESTMessageV2('');

	// parm1 is target system - use incident table API and retrieve the created incident number
    r.setEndpoint(event.parm1+'/api/now/table/incident?sysparm_fields=number');
    r.setHttpMethod("post");

	//update to use properties 
    r.setBasicAuth("pete", "Int3rn3t@)@!");
	r.setRequestHeader('Content-Type','application/json');
	r.setRequestBody('{"short_description":"'+current.short_description+'",' +'"description":"'+current.description+'",' +'"correlation_id":"'+current.number+'"}');


	//post the incident to target system 
    var response = r.execute();
    var responseBody = response.getBody();
	var json = responseBody;
    var obj = JSON.parse(json); //define JSON parsing for the response JSON file to decode
    var remoteIncidentNumber = obj.result.number; //decoded dev2 newly created incident 

	//set the correlation ID
    current.correlation_id = remoteIncidentNumber;
	current.update();
	var httpStatus = response.getStatusCode();
} catch (ex) {
    gs.info('script action interest open incident app gave error: ' + ex.message);
}
