sysevent_script_action.script:
gs.info('PS: running script action');

try {

    // get record from target table
    var endPoint = new GlideRecord('x_653938_interest_target');
    endPoint.get(event.parm1);


    // setup REST message to send over the newly created incident
    var r = new sn_ws.RESTMessageV2('');


    var fields = new GlideRecord('x_653938_interest_fields');
    var requestBody = {};
    fields.addQuery('module', current.sys_class_name);
    //gs.info('PS current is : '+current.sys_class_name);
    fields.query();
    while (fields.next()) {
        gs.info('Script field name is ' + fields.field_name);

        switch (fields.field_name.toString()) {
            case 'short_description':
                requestBody.short_description = current.short_description.toString();
                break;
            case 'description':
                requestBody.description = current.description.toString();
                break;
            case 'correlation_id':
                requestBody.correlation_id = current.number.toString();
                break;
        }
    }
    var str = global.JSON.stringify(requestBody);
    gs.info('PS action script requestBody is ' + str);


    // parm1 is target table record - use incident table API and retrieve the created incident number
    // r.setEndpoint(endPoint.getValue('target_url') + '/api/now/table/incident?sysparm_fields=number');


    //credentials 
    var user = endPoint.getValue('restuser');
    var pwrd = endPoint.getValue('restpwd');

    gs.info('creds ' + user + ' / ' + pwrd);
    r.setEndpoint(endPoint.getValue('target_url') + '/api/now/table/incident?sysparm_fields=number');
    r.setBasicAuth(user, pwrd);
    r.setRequestHeader('Content-Type', 'application/json');
    r.setRequestBody(global.JSON.stringify(requestBody));
    r.setHttpMethod("post");
    r.setLogLevel('all');
   // r.setRequestBody('{"short_description":"' + current.short_description + '",' + '"description":"' + current.description + '",' + '"correlation_id":"' + current.number + '"}');


	//var str = global.JSON.stringify(requestBody);
  //  gs.info('PS action script test  requestBody is ' + );
    //post the incident to target system 
    var response = r.execute();
    var httpStatus = response.getStatusCode();
    gs.info('Action script http status is ' + httpStatus);


	var retryCount = parseInt(event.parm2) + 1; 
    if (httpStatus != '201' && retryCount<4){
        gs.info('Response / Retry attempt ' + httpStatus+ ' / ' +retryCount );
        var retryTime = new GlideDateTime();
        retryTime.addSeconds(60);
        gs.eventQueueScheduled('x_653938_interest.open', current, event.parm1, retryCount, retryTime);
    }


    //process the reponse data
    var responseBody = response.getBody();
    var json = responseBody;
    var obj = JSON.parse(json); //define JSON parsing for the response JSON file to decode
    var remoteIncidentNumber = obj.result.number; //dev2 newly created incident 
    gs.info('remoteIncidentNumber is : ' + remoteIncidentNumber);
    //set the correlation ID
    current.correlation_id = remoteIncidentNumber;
    current.update();


} catch (ex) {
    gs.info('script action interest open incident app gave error: ' + ex.message);
}
