sysevent_script_action.script:
gs.info('PS: running script action');

try {
    var postInc = new InteRESTIncidentUtils(event.parm1);
    var postResult = postInc.open(current);
	var retryCount = parseInt(event.parm2) + 1;
    gs.info('SA - include retured ' + postResult);
    if (!postResult) {
        if (retryCount > 2) {
            var addTimeOut = new GlideRecord('x_653938_interest_inctimeout');
            addTimeOut.eventname = event.name;
            addTimeOut.incident = current.number.toString();
			addTimeOut.insert();
			gs.info('SA timeout record written ');
        } else {
			gs.info('retrying in 60 secs');
            var retryTime = new GlideDateTime();
            retryTime.addSeconds(60);
            gs.eventQueueScheduled('x_653938_interest.open', current, event.parm1, retryCount, retryTime);
			}
	}

}

   catch (ex) {
        gs.info('script action interest open incident app gave error: ' + ex.message);
    }
