sysevent_script_action.script:
gs.info('PS: running script action');

try {
    var r = new sn_ws.RESTMessageV2('');
    r.setEndpoint(event.parm1+'/api/now/table/incident?sysparm_fields=number&short_descrption='+current.short_description+'&correlation_id='+current.correlation_id+'&description='+current.description);
    r.setHttpMethod("post");
    r.setBasicAuth("pete", "Int3rn3t@)@!");
//     r.setStringParameterNoEscape("correlation_id", current.number);
//     r.setStringParameterNoEscape("short_description", current.short_description);
//     r.setStringParameterNoEscape("description", current.description);
// 	r.setRequestHeader("Accept","application/json");
	//r.setQueryParameter('sysparm_fields','number');

    var response = r.execute();
    var responseBody = response.getBody();
	gs.info('AS line 15 '+response.getErrorMessage());
    var json = responseBody;
    var obj = JSON.parse(json); //define JSON parsing for the response JSON file to decode
    var foundNumber = obj.result.number; //decoded dev2 newly created incident 
	gs.info('PS foundNumber is : '+ foundNumber);

    current.correlation_id = foundNumber;
    gs.info("The generated sysid from Dev1 instance is :  " + foundNumber); //log sys_id		
    var httpStatus = response.getStatusCode();
} catch (ex) {
    gs.info('script action interest open incident app gave error: ' + ex.message);
}
